package oct;

//Simple Calculator using the SOP statements
public class Lab003 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		int num1=20, num2=20;
		
		System.out.println(num1 + " + " + num2+ " = " + (num1 + num2));
	    System.out.println(num1 + " - " + num2 + " = " + (num1 - num2));
	    System.out.println(num1 + " * " + num2 + " = " + (num1 * num2));
	    System.out.println(num1 + " / " + num2 + " = " + (num1 / num2));	
	}

}
